import React, { useState } from "react";

const InputComponent = () => {
  const [text, setText] = useState("");
  const [image, setImage] = useState(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  const handleSave = () => {
    // Create an object with text and image data
    const data = {
      text: text,
      image: image,
    };
    // You can do whatever you want with the data here, like sending it to a server

    // For demonstration, let's just log it to the console
    console.log(data);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Enter text..."
        value={text}
        onChange={handleTextChange}
      />
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleSave}>Upload on facebook</button>
    </div>
  );
};

export default InputComponent;
